class Solution {
    public TreeNode buildTree(int[] preorder, int[] inorder) {
        TreeNode res=new TreeNode(preorder[0]);
        dfs(res,preorder,inorder,0,preorder.length-1,0);
        return res;
    }
    //i,j:node在中序数组的范围          k:node在前序数组中的坐标
    void dfs(TreeNode node,int[] preorder, int[] inorder,int i,int j,int k){
        if(node==null){
            return;
        }
        int index=0;
        for (; index < inorder.length; index++) {
            if(inorder[index]==node.val){
                break;
            }
        }
        int left_num=index-i;
        int right_num=j-index;
        if(left_num>0){
            node.left=new TreeNode(preorder[k+1]);
        }
        if(right_num>0){
            node.right=new TreeNode(preorder[k+left_num+1]);
        }
        dfs(node.left,preorder,inorder,i,index-1,k+1);
        dfs(node.right,preorder,inorder,index+1,j,k+left_num+1);
    }
}
