class Solution {
    public int magicTower(int[] nums) {
        int cnt=0;
        long sum=1,delay=0;
        Queue<Integer> heap=new PriorityQueue<>() ;
        for (int i = 0; i < nums.length; i++) {
            if(nums[i]<0){
                heap.offer(nums[i]);
            }
            sum+=nums[i];
            if(sum<=0){
