class Solution {
    public int[] inventoryManagement(int[] stock, int cnt) {
        int len=stock.length;
        for (int i = 0; i < len; i++) {
            for (int j = i+1; j < len; j++) {
                if(stock[j]<stock[i]){
                    int temp=stock[i];
                    stock[i]=stock[j];
                    stock[j]=temp;
                }
            }
        }
        int[]res=new int[cnt];
        for (int i = 0; i < cnt; i++) {
            res[i]=stock[i];
        }
        return res;
    }
}
```

