class Solution {
    public int wiggleMaxLength(int[] nums) {
        int len= nums.length;
        if(len==1){
            return 1;
        }
        if(len==2&&nums[0]!=nums[1]){
            return 2;
        }else if(len==2){
            return 1;
        }
        int[] arr=new int[len-1];
        for (int i = 0; i < len-1; i++) {
            arr[i]=nums[i+1]-nums[i];
        }
        int index=0;
        while(index<len-1&&arr[index]==0){
            index++;
        }
        if(index==len-1){
            return 1;
        }
        int flag=arr[index],cnt=2;
        for (int i = index+1; i < len-1; i++) {
            if(flag*arr[i]<0){
                cnt++;
                flag=arr[i];
            }
        }
        return cnt;
    }
}
```

