                }else{
                    return o1.no-o2.no;
                }
            }
        };
        Collections.sort(list,comp);

        for (int i = 0; i < list.size(); i++) {
            int cnt=list.get(i).no;
            int index=0;
            while(cnt>0&&index<=i){
                if(list.get(index).height>=list.get(i).height){
                    cnt--;
                }
                index++;
            }
            while (list.get(index).height<list.get(i).height){
                index++;
            }
            list.add(index,list.get(i));
            list.remove(i+1);
        }
        for (int i = 0; i < people.length; i++) {
            people[i][0]=list.get(i).height;
            people[i][1]=list.get(i).no;
        }
        return people;
    }
}
