class Solution {
    public int maxSubArray(int[] nums) {
        int len= nums.length,sum=0,max=Integer.MIN_VALUE;
        if(len==1){
            return nums[0];
        }
        int idx=0;
        while(idx<len){
            sum+=nums[idx];
            if(max<sum){
                max=sum;
            }
            if(sum<0){
                sum=0;
            }
            idx++;
        }
        return max;
    }
}
```

