class Solution {
    List<String>res=new ArrayList<>();
    LinkedList<Integer>path=new LinkedList<>();
    public List<String> restoreIpAddresses(String s) {
        backtraking(0,s);//
        return res;
    }
    void backtraking(int start,String s){//
        if(path.size()==4&&start==s.length()){
            res.add(build((path)));//
            return;
        }
        if(path.size()>4){
            return;
        }
        for (int i = start+1; i <=s.length(); i++) {
            if(s.substring(start,i).length()>1&&s.substring(start,i).charAt(0)=='0'){
                break;
            }
            int temp=convert(s.substring(start,i));
            if(temp<=255&&temp>=0){
                path.add(temp);
                backtraking(i,s);
                path.removeLast();
            }
        }
    }
    String build(LinkedList<Integer> al){
        StringBuilder sb=new StringBuilder();
        sb.append(al.get(0));
        for (int i = 1; i < 4; i++) {
            sb.append('.');
            sb.append(al.get(i));
        }
        return sb.toString();
    }
    int convert(String str){
        int out=0,k=1,i=str.length()-1;
        while(i>=0){
            out+=k*(str.charAt(i)-'0');
            k*=10;
            i--;
        }
        return out;
    }
}
