class Solution {
    public String removeDuplicates(String s) {
        Stack<Character> stack=new Stack<>();
        char last;
        for (char ch:
                s.toCharArray()) {
            if(stack.empty()){
                stack.push(ch);
                last=ch;
            }else if(ch==stack.peek()){
                stack.pop();

            }else{
                stack.push(ch);
            }
        }
        Stack<Character> stack_=new Stack<>();
        while(!stack.empty()){
            stack_.push(stack.pop());
        }
        StringBuilder sb=new StringBuilder();
        while(!stack_.empty()){
            sb.append(stack_.pop());

        }
        return sb.toString();
    }
}
