class Solution {
    public int removeElement(int[] nums, int val) {
        int len= nums.length;
        if(len==1&&nums[0]!=val){
            return 1;
        }else if(len==1){
            return 0;
        }
        int i=0,j=len-1;
        while(i<j){
            while(i<j&&nums[i]!=val){
                i++;
            }
            while(i<j&&nums[j]==val){
                j--;
            }
            if(i>=nums.length||j<0||i>=j){
                break;
            }
            int temp=nums[i];
            nums[i]=nums[j];
            nums[j]=temp;
            i++;
            j--;
            if(i>=nums.length||j<0||i>=j){
                break;
            }
        }
        int index=nums.length;
        for (int k = 0; k < nums.length; k++) {
            if(nums[k]==val){
                index=k;
                break;
            }
        }
        return index;
    }
}
```



