class Solution {
    public int removeDuplicates(int[] nums) {
        int len= nums.length;
        if(len==1){
            return 1;
        }
        int i=0,j=1;
        while(j< len){
            while(j< len&&nums[j]==nums[i]){
                j++;
            }
            if(j>=len){
                break;
            }
            int temp=nums[i+1];
            nums[i+1]=nums[j];
            nums[j]=temp;
            i++;
