class Solution {
    public ListNode removeNthFromEnd(ListNode head, int n) {
        if(n<=0||head.next==null&&n==1){
            return null;
        }
        ListNode fast=head,slow=head;
        for (int i = 0; i < n; i++) {
            fast=fast.next;
        }
        if(fast==null){
            return head.next;
        }else{
            fast=fast.next;
        }
        while(fast!=null){
            fast=fast.next;
            slow=slow.next;
        }
        slow.next=slow.next.next;
        return head;
    }
}
