class Solution {
    public ListNode removeNthFromEnd(ListNode head, int n) {
        int cnt=0;
        ListNode p=head;
        while(p!=null){
            cnt++;
            p=p.next;
        }
        if(n==cnt){
            return head.next;
        }
        p=head;
        while(p!=null){
            if(cnt-1==n){
                p.next=p.next.next;
            }
            cnt--;
            p=p.next;
        }
        return head;
    }
}
```



