public class Solution {
    public ListNode getIntersectionNode(ListNode headA, ListNode headB) {
        Stack<ListNode> a=new Stack<>();
        Stack<ListNode> b=new Stack<>();
        ListNode pa=headA,pb=headB,res=headA;
        int flag=0;
        while(pa!=null){
            a.push(pa);
            pa=pa.next;
        }
        while(pb!=null){
            b.push(pb);
            pb=pb.next;
        }
        int cnt=0;
        while(!a.empty()&&!b.empty()){
            ListNode ap=a.pop(),bp=b.pop();
            if(ap!=null&&ap==bp){
                cnt++;
                res=ap;
                flag=1;
            }
        }
        if(flag==1){
            return res;
        }else{
            return null;
        }

    }
}
```

