```java
public class Solution {
    public ListNode detectCycle(ListNode head) {
        ListNode p=head;
        ArrayList<ListNode>al=new ArrayList<>();
        while(p!=null){
            if(al.contains(p)){
                return p;
            }
            al.add(p);
            p=p.next;
        }
        return null;
    }
}
